import React from "react";
import { useState, useEffect,useRef} from "react";

import { validateFields } from '../herpers/validateFields';

import { Layout } from "../components/commons/Layout";
import { Modal } from "../components/commons/Modal";
export const Newsletter = () => {

  const [message, setMensaje] = useState(null);
  const inputRef = useRef(null);
  //const [newsletterFile, setNewsletterFile] = useState(null);
  const [newsletter, setNesletter] = useState({
    newsletterName: "",
    newsletterDescription: "",
    newsletterType:"",
    newsletterFile:null
  });

  useEffect(() => {
    setMensaje(null);
  }, [newsletter]);
  
  const handlerFile = (e) => {
    if (e.target.files.length < 1) {
      setMensaje("You need to provide a file");
      return;
    } else {
      //setFile(e.target.files[0]);
      newsletter.newsletterFile=e.target.files[0];
    }
  };
  const handlerSelect = (e) => {
    newsletter.newsletterType = e.target.value;   
  };

  const handlerForm = (e) => {
    e.preventDefault();
    let valueMessage = validateFields(newsletter);
    setMensaje(valueMessage);

    if (valueMessage !== "") {
      return;
    }

    let paramNewsletter = new FormData();
    paramNewsletter.append("newsletter", newsletter);

    console.log("Validation ok");
   // sendTransaction(params, inputRef, setFile, setLoading);

  };
 /* const sendNewsletter = () => {
  
    console.log("sendNewsletter");
  };*/

  return (
    <div>
      <Layout>
        <form className="container" onSubmit={ handlerForm }>
          <div className="row">
            <div className="col-6 offset-3">
              <h5 className="mb-5"> Newsletter Details </h5>

              <div className="form-group ">
                <label htmlFor="newsletterTitle" className="form-label ">
                  Newsletter Title
                </label>
                <input
                  className="form-control mb-3"
                  id="newsletterTitle"
                  name="newsletterTitle"
                  placeholder="Introduce newsletter title"
                  type="text"
                />
              </div>
              <div className="form-group ">
                <label htmlFor="newsletterDescrip" className="form-label ">
                  Newsletter Description
                </label>
                <input
                  className="form-control mb-3"
                  id="newsletterDescrip"
                  name="newsletterDescrip"
                  placeholder="Introduce newsletter Description"
                  type="text"
                />
              </div>
              <div className="col-md-4">
                <label htmlFor="inputState" className="form-label">
                  Newsletter Type
                </label>
                <select
                  id="inputState"
                  className="form-select  mb-3"
                  defaultValue={"DEFAULT"}
                  onClick= { handlerSelect }
                >
                  <option value="DEFAULT" disabled>
                    Select newsletter type
                  </option>
                  <option value="1">Artistic</option>
                  <option value="2">Sports</option>
                </select>
              </div>

              <div className="form-group mb-3">
                <label htmlFor="newsletterFile" className="form-label">
                  Upload Newsletter File (.png/.pdf)
                </label>
                <input
                  className="form-control"
                  id="newsletterFile"
                  name="newsletterFile"               
                  type="file"
                  ref={inputRef}
                 onChange={ handlerFile }
                />
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-2 offset-3">
              <button
                type="submit"
                className="btn btn-primary  mt-3 mb-5"
                onClick={sendNewsletter}
              >
                Save Newsletter
              </button>
            </div>
            <div className="col-2 ">
              <button
                type="submit"
                data-bs-toggle="modal"
                data-bs-target="#sendNesletter"
                className="btn btn-primary  mt-3 mb-5"
                onClick={sendNewsletter}
              >
                Send Newsletter
              </button>
            </div>
          </div>
        </form>
        <Modal title="Newsletter- Email configuration" id="sendNesletter" />
      </Layout>
    </div>
  );
};
