import React from "react";
import { useEffect, useContext } from "react";
import { NewsletterContext } from "../contexts/newsletterContext";

export const Unsuscribed = () => {
  const { suscribedList, getAllSuscribed } = useContext(NewsletterContext);
  let email = "mipv1687@hotmail.com";
  useEffect(() => {
    getAllSuscribed(email);
  }, []);

  let unsuscribedList;
  const unsuscribedHandler = (e) => {
    let checks =  document.querySelectorAll('.form-check-input');
    unsuscribedList = "";
    checks.forEach((e) => {
        if (e.checked ==true){
            unsuscribedList += e.value + " ";
        }
    });

    console.log(""+unsuscribedList);

  }
  suscribedList.map((index) => console.log(index));

  return (
    <div className="card mt-3">
      <div className="card-header">Unsuscribed</div>
      <div className="card-body">
        <h5 className="card-title">We will miss you</h5>
        <p className="card-text">
          If you are sure to unsubscribe, you can to continue the process
        </p>
        {suscribedList.map((index) => (
          <>
            <div className="form-check">
              <input
                className="form-check-input"
                type="checkbox"
                value={index[0]}
                id="flexCheckDefault"
              />
              <label className="form-check-label" htmlFor="flexCheckDefault">
                {index[2]}
              </label>
            </div>
          </>
        ))}
        <button type="buttom" className="btn btn-primary  mt-3 mb-5" onClick={unsuscribedHandler}>
          Unsuscribed
        </button>
        <ul id="listaCheck"></ul>
      </div>
    </div>
  );
};
