export const validateFields = ({ newsletterTitle, newsletterDescrip,newsletterType,newsletterFile }) => {
  if (newsletterTitle === "") return "Name are required";

  if (newsletterDescrip === "") return "Email are required";

  if (newsletterType < 1 || typeof newsletterType === "undefined")
    return "You need to provide a Type for newsletter";

  if (newsletterFile === null || typeof newsletterFile === "undefined")
    return "You need to provide a file";
  
  if (newsletterFile.type !== "pdf/png") return "We only support .pdf/ .png";

  if (newsletterFile.size > 2000000) return "The file is too large";

  return "";
};
