import React, { createContext, useReducer } from 'react';
import NewsletterReducer from '../reducer/NewsletterReducer';
import {GET_NEWSLETTERS} from '../const/actionTypes';

export const NewsletterContext = createContext();

export const NewsletterContextProvider = props => {
   
    const inicialState = {
        newsletterList: []
    };

    //use reducer pra editar, crear eliminar, etc
    //dispathc método logado al state y que nos, es mediante ese método que indicaremos las acciones a realizar
    //para que realice el cambio en el valor del estado
    const [state, dispatch] = useReducer(NewsletterReducer,inicialState);

    //obtener todos los rows

    const getNewsletters = () => {
        const newsletters = [
            {
                "newsletterId": 5,
                "newsletterTypeId": 1,
                "newsletterName": "BigBang",
                "newsletterDescription": "It's about your art, not about you.",
                "newsletterAttach": "artToday.png",
                "sentDate": "2022-10-29T19:14:03.477155",
                "sentEstatus": 1,
                "creationDate": "2022-10-29T18:22:09.054873"
            }    ,
            {
                "newsletterId": 6,
                "newsletterTypeId": 2,
                "newsletterName": "Motivational Sports",
                "newsletterDescription": "If you love the game, you know it takes true dedication.",
                "newsletterAttach": "sportMessages.png",
                "sentDate": null,
                "sentEstatus": 0,
                "creationDate": "2022-10-29T22:58:26.893728"
            }
        ];
        dispatch({
            type:GET_NEWSLETTERS, //que tipo es
            payload:newsletters//que o cual es el valor que necesitamos modificar en el state
        })
    };

    //el estado que queremos compartir
    return (
        <NewsletterContext.Provider
          value={{
           
            newsletterList: state.newsletterList,   
            
            
            getNewsletters       
          }}
        >
          {props.children}
        </NewsletterContext.Provider>
      );
}
