import React, { createContext, useReducer } from "react";
import NewsletterReducer from "../reducer/NewsletterReducer";
import {
  GET_NEWSLETTERS,
  ADD_NEWSLETTER,
  ADD_MAIL_USER,
  GET_ALL_NEWSLETTER_TYPE,
  GET_ONE_NEWSLETTER,
  GET_ALL_SUSCRIBED,
} from "../const/actionTypes";
import Axios from "axios";
import Swal from "sweetalert2";
import { API_BASE_URL } from "../config";

export const NewsletterContext = createContext();

export const NewsletterContextProvider = (props) => {
  const inicialState = {
    newsletterList: [],
    newsletterTypeList: [],
    suscribedList: [],
    currentData: {},
    currentDataList: {},
  };
  function showAlert(icon, details) {
    Swal.fire({
      icon: icon,
      title: details.message,
      text: details.details,
    });
  }
  //use reducer pra editar, crear eliminar, etc
  //dispathc método logado al state y que nos, es mediante ese método que indicaremos las acciones a realizar
  //para que realice el cambio en el valor del estado
  const [state, dispatch] = useReducer(NewsletterReducer, inicialState);

  const messageOk = {
    message: "Success",
    details: "Data saved successfully",
  };
  //obtener todos los rows

  const getNewsletters = async () => {
    try {
     
      const url =  `${API_BASE_URL}/newsletter`;
      const response = await fetch(url, {
        method: "GET"
      });
      const data = await response.json();  
      dispatch({
        type: GET_NEWSLETTERS, 
        payload: data, 
      }); if (!response.ok) {    
        showAlert("error", data);
      } 
    } catch (error) {
      const errorException = {
        message: "We have a problem",
        details: error.message,          
      }
     
    showAlert("error", errorException);
    }
  };
  const getAllNewsletterType = async () => {
    try {
      const url =  `${API_BASE_URL}/newsletterType`;
      const response = await fetch(url, {
        method: "GET"
      });
      const data = await response.json(); 
      dispatch({
        type: GET_ALL_NEWSLETTER_TYPE, 
        payload: data, 
      });
      if (!response.ok) {    
        showAlert("error", data);
      } 
    } catch (error) {
      const errorException = {
        message: "We have a problem",
        details: error.message,          
      }
     
    showAlert("error", errorException);
    }
  };

  const getAllSuscribed = async (email) => {
    try {
      let res = await Axios.get(`/newsletterUser/${email}`);
      dispatch({
        type: GET_ALL_SUSCRIBED, //que tipo es
        payload: res.data, //que o cual es el valor que necesitamos modificar en el state
      });
    } catch (error) {
      console.log(error);
    }
  };

  const getNewsletter = async (newsletter) => {
    try {
      dispatch({
        type: GET_ONE_NEWSLETTER,
        payload: newsletter,
      });
    } catch (error) {
      console.log(error);
    }
  };

  const addNewsletter = async (newsletter, setLoading) => {
    try {
      setLoading(true);
      const url = `${API_BASE_URL}/newsletter`;
      const response = await fetch(url, {
        method: "POST",
        body: newsletter,
      });
      const data = await response.json();
      dispatch({
        type: ADD_NEWSLETTER,
        payload: data,
      });
      if (response.ok) {
        setLoading(false);
        showAlert("success", messageOk);
      } else {
        setLoading(false);
        showAlert("error", data);
      }
    } catch (error) {
      const errorException = {
        message: "We have a problem",
        details: error.message,
      };
      setLoading(false);
      showAlert("error", errorException);
    }
  };

  const addEmailUser = async (paramsEmailUser, sendList, setLoading) => {
    let url = "";
    try {
      setLoading(true);

      if (sendList === 1) url = `${API_BASE_URL}/users/uploadFile`;
      else url = `${API_BASE_URL}/users/user`;

      const response = await fetch(url, {
        method: "POST",
        body: paramsEmailUser,
      });
      const data = await response.json();
      dispatch({
        type: ADD_MAIL_USER,
        payload: data,
      });
      if (response.ok) {
        setLoading(false);
        showAlert("success", messageOk);
      } else {
        setLoading(false);
        showAlert("error", data);
      }
    } catch (error) {
      const errorException = {
        message: "We have a problem",
        details: error.message,
      };
      setLoading(false);
      showAlert("error", errorException);
    }
  };
  const addNewsletterType = async (nesletterType) => {
  
    try {
      const url = `${API_BASE_URL}/newsletterType`;
      const response = await fetch(url, {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: nesletterType,
      });
      const data = await response.json();      
      if (response.ok) {
        showAlert("success", messageOk);
      } else {
        showAlert("error", data);
      }
    } catch (error) {
      const errorException = {
        message: "We have a problem",
        details: error.message,
      };
      showAlert("error", errorException);
    }
  };

  const unsuscribedNewsletter = async (unsuscribedList) => {
    if (unsuscribedList.length < 0) return;

    let data = {};
    try {
      let res = await Axios.put("/newsletterUser", unsuscribedList);
      let data = res.data;
      window.location.reload();
      /*  dispatch({
            type: ADD_NEWSLETTER, //que tipo es
            payload: data //que o cual es el valor que necesitamos modificar en el state
          });
          */
      showAlert("success", "Unsuscribed Ok");
    } catch (error) {
      console.log("error:" + error);
      if (data.length === 0) {
        const errorException = {
          message: "We have a problem",
          details: error.message,
        };
        showAlert("error", errorException);
      } else showAlert("error", data);
    }
  };

  return (
    <NewsletterContext.Provider
      value={{
        currentData: state.currentData,
        newsletterList: state.newsletterList,
        newsletterTypeList: state.newsletterTypeList,
        currentDataList: state.currentDataList,
        suscribedList: state.suscribedList,

        getNewsletters,
        addNewsletter,
        addNewsletterType,
        addEmailUser,
        getAllNewsletterType,
        getNewsletter,
        getAllSuscribed,
        unsuscribedNewsletter,
       
      }}
    >
      {props.children}
    </NewsletterContext.Provider>
  );
};
