import React from "react";
import {NewsletterContext,NewsletterContextProvider} from '../../contexts/newsletterContext';
import { useState,useContext,useRef} from "react";
import { validateEmailConfig } from '../herpers/validateEmailConfig';

export const Modal = ({title, id}) => {

  const { currentData} = useContext ( NewsletterContext );
    const [disabledUser,setDisabledUser] = useState(false);
    const [disabledFile,setDisabledFile] = useState(true);
    const [sendList,setSendList] = useState(0);
    const [message, setMensaje] = useState("");
    const [file, setFile] = useState(null);
    const inputRef = useRef(null);
      paramNewsletter.append("file", file);
    const [user, setUser] = useState({
      
      userName: "",
      userEmail: "",
    });
    const { userName, userEmail } = user;
    
    const closeModal = () => {
        console.log ("closeModal");
    }
    const handlerSendList = () => {
        console.log (sendList);
        if(sendList===0){
            setSendList(1);
            setDisabledFile(false);
            setDisabledUser(true);
           

        }
        else{
            setSendList(0);
            setDisabledFile(true);
            setDisabledUser(false)
          
        }
       
    }
   const  handlerSendModal = () => {
    e.preventDefault();
    console.log("MODAL ");   
    console.log(currentData); 

    const handlerFile = (e) => {
      console.log(e.target.files[0]);
      if (e.target.files.length < 1) {
        console.log(e.target.files[0]);
        setMensaje("You need to provide a file 1");
        return;
      } else {
        setFile(e.target.files[0]);
      }
    };
  

    if (currentData.length !== 0 ){
      let {
        objectInsert:  {
         newsletterId,
         newsletterTypeId}
      }= currentData;

      let valueMessage = validateEmailConfig(user,file);
      setMensaje(valueMessage);
  
      if (valueMessage !== "") {
        return;
      }
     

    const newsletterDetails = {
      newsletterId:newsletterId,
      sendList:sendList,
      name:userName,
      email:userEmail
    }
      let params = new FormData();
      params.append("newsletterDetails", newsletterDetails);    
      params.append("file", file);

    }
   }

   
    
    

  return (
   
    <>
     <NewsletterContextProvider>
      <div className="modal fade " tabIndex="-1" id={id}  name={id} >
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">{title}</h5>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="modal"
              aria-label="Close"
            ></button>
          </div>
          <div className="modal-body">
            <div className="row">            
                
                <div className="form-group ">
                  <label htmlFor="userName" className="form-label ">
                    Name
                  </label>
                  <input
                    className="form-control mb-3"
                    id="userName"
                    name="userName"
                    placeholder="Introduce name"
                    type="text"
                    disabled={disabledUser}
                   
                  />
                </div>
                <div className="form-group mb-3">
                  <label htmlFor="userEmail" className="form-label">
                    Email
                  </label>
                  <input
                    className="form-control"
                    id="userEmail"
                    name="userEmail"
                    placeholder="Introduce email"
                    type="email"
                    disabled={disabledUser}
                   
                  />
                </div>
                <div className="mb-3 ms-3 form-check">
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id="checkList"
                    onClick={ handlerSendList }
                  />
                  <label className="form-check-label" htmlFor="checkList">
                    Send user list
                  </label>
                </div>

                <div className="form-group mb-3">
                  <label htmlFor="file" className="form-label">
                    Upload a CSV File
                  </label>
                  <input
                    className="form-control"    
                    id="file"
                    name="file"
                    ref={inputRef}
                    type="file"
                    onClick={handlerFile}
                    disabled={disabledFile}
                  />
                </div>             
            </div>
          </div>
          <div className="modal-footer">            
            <button type="button" className="btn btn-primary"   onClick={ handlerSendModal }>
              Send Now
            </button>
            <button
              type="button"
              className="btn btn-secondary"
              data-bs-dismiss="modal"
              onClick={ closeModal }
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
    </NewsletterContextProvider>  
    </>
  );
};
