export const validateFields = ({ newsletterTitle, newsletterDescrip }, file) => {
  if (newsletterTitle === "") return "Name are required";

  if (newsletterDescrip === "") return "Email are required";

  if (file === null || typeof file === "undefined")
    return "You need to provide a file";

  if (file.type !== "pdf/png") return "We only support .pdf/ npg";

  if (file.size > 2000000) return "The file is too large";

  return "";
};
