import { GET_NEWSLETTERS, ADD_NEWSLETTER,GET_ALL_NEWSLETTER_TYPE,GET_ONE_NEWSLETTER,GET_ALL_SUSCRIBED} from "../const/actionTypes";
//funciones reductoras (ststae y tipo de accion que queremos realizar editar, crear, eliminar)
export default (state,action) => {
  switch (action.type) {
    case GET_NEWSLETTERS:
      return {
        ...state,
        newsletterList: action.payload,
      };

    case ADD_NEWSLETTER:
      return {
        ...state,
        currentData:  action.payload,
      };
      case GET_ALL_NEWSLETTER_TYPE:
        return {
          ...state,
          newsletterTypeList:  action.payload,
        };
        case GET_ONE_NEWSLETTER:
          return {
            ...state,
            currentData:  action.payload
          };
          case GET_ALL_SUSCRIBED:
        return {
          ...state,
          suscribedList:  action.payload,
        };
       
    default:
      return state;
  }
};
