import {GET_NEWSLETTERS,ADD_NEWSLETTER} from '../const/actionTypes';
//funciones reductoras (ststae y tipo de accion que queremos realizar editar, crear, eliminar)
export default (state, action) => {
  switch (action.type) {
    case GET_NEWSLETTERS:
        return {
            ...state,
            newsletterList:action.payload
        };
        case ADD_NEWSLETTER: 
        return ({
            ...state,
            newsletterList: [...state.newsletterList, action.payload]
        });
       
    default:
      return state;
  }
};
