import Axios from "axios";
import Swal from "sweetalert2";
import { API_BASE_URL } from '../config';

function showAlert(icon, details) {
  Swal.fire({
    icon: icon,
    title: details.message,
    text: details.details,
  });
}

export const createNesletterType2 = async (nesletterType) => {
  let data = {};
  console.log(nesletterType);
  try {
    let res = await Axios.post("/newsletterType", nesletterType);
    data = res.data;
    console.log(res);
    console.log(data);
    showAlert("success", data);
  } catch (error) {
    if( data.length === 0 ) {
        const errorException = {
          message: "We have a problem",
          details: error.message,          
        };
            showAlert("error", errorException);
      }
      else
        showAlert("error", data);
  }
};

export const createNewsletterType = async (nesletterType) => {
  try {
    console.log(nesletterType);
    console.log(JSON.stringify(nesletterType));
   // setLoading(true);
    const url =  `${API_BASE_URL}/newsletterType`;
    const response = await fetch(url, {
      method: "POST",
      body: nesletterType
    });
    const data = await response.json();
   
    console.log(data);
    if (response.ok) {
      showAlert("success", data);
      //setLoading(false);
    } else {
      showAlert("error", data);
      //setLoading(false);
    }
  } catch (error) {
    const errorException = {
      message: "We have a problem",
      details: error.message,          
    };
    showAlert("error", errorException);
    //setLoading(false);
  }
};
