import React from "react";
import { useState, useEffect,useRef,useContext} from "react";
import { validateFields } from '../herpers/validateFields';
import { NewsletterContext } from  '../contexts/newsletterContext';

import { Modal } from "../components/commons/Modal";
export const Newsletter = () => {

  
  const { addNewsletter,currentNewsletter } = useContext ( NewsletterContext );
  //const [newsletterFile, setNewsletterFile] = useState(null);
  const [newsletter, setNesletter] = useState({
    newsletterName: "",
    newsletterDescription: "",
    newsletterType:""
  });
  const { newsletterName, newsletterDescription} = newsletter;
  const [message, setMensaje] = useState("");
  const inputRef = useRef(null);
  const [sendDisabled, setSendDisabled] = useState(true);
  const [file, setFile] = useState(null);

  useEffect(() => {
    setMensaje("");
  }, [newsletter,file]);
  
  const handlerNewsletter = ({ target }) => {
    setNesletter({
      ...newsletter,
      [target.name]: target.value,
    });
  };
 const handlerSelect = (e) => {
    newsletter.newsletterType = e.target.value;   
  };
  const handlerFile = (e) => {
    console.log(e.target.files[0]);
    if (e.target.files.length < 1) {
      console.log(e.target.files[0]);
      setMensaje("You need to provide a file 1");
      return;
    } else {
      setFile(e.target.files[0]);
    }
  };


  const handlerForm = (e) => {
    
    e.preventDefault();


    let valueMessage = validateFields(newsletter,file);
    setMensaje(valueMessage);

    if (valueMessage !== "") {
      return;
    }

    let paramNewsletter = new FormData();
    paramNewsletter.append("newsletterName", newsletter.newsletterName);
    paramNewsletter.append("newsletterDescription", newsletter.newsletterDescription);
    paramNewsletter.append("newsletterType", newsletter.newsletterType);
    paramNewsletter.append("file", file);

    console.log("Validation ok");
    
    setSendDisabled(false);
    addNewsletter(paramNewsletter);
    console.log("uno"); 
    console.log(currentNewsletter); 
    console.log("dos"); 
   
   // if(currentNewsletter.objectInsert)
    //addNewsletter(newsletter);
   
   // sendTransaction(params, inputRef, setFile, setLoading);

  };
 const sendNewsletter = () => {
  
    console.log("send Newsletter ");
  };

  return (
    <div>
    
        <form className="container" onSubmit={ handlerForm }>
          <div className="row">
            <div className="col-6 offset-3">
              <h5 className="mb-5"> Newsletter Details </h5>
              {message && (
            <div className="alert alert-danger mt-3" role="alert">
              {message}
            </div>
          )}
              <div className="form-group ">
                <label htmlFor="newsletterName" className="form-label ">
                  Newsletter Title
                </label>
                <input
                  className="form-control mb-3"
                  id="newsletterName"
                  name="newsletterName"
                  placeholder="Introduce newsletter title"
                  type="text"
                  value={newsletterName}
                  onChange={handlerNewsletter}
                />
              </div>
              <div className="form-group ">
                <label htmlFor="newsletterDescrip" className="form-label ">
                  Newsletter Description
                </label>
                <input
                  className="form-control mb-3"
                  id="newsletterDescription"
                  name="newsletterDescription"
                  placeholder="Introduce newsletter Description"
                  type="text"
                  value={newsletterDescription}
                  onChange={handlerNewsletter}
                />
              </div>
              <div className="col-md-4">
                <label htmlFor="newsletterType" className="form-label">
                  Newsletter Type
                </label>
                <select
                  id="newsletterType"
                  className="form-select  mb-3"
                  defaultValue={"DEFAULT"}
                  onChange={handlerSelect}
                 
                >
                  <option value="DEFAULT" disabled>
                    Select newsletter type
                  </option>
                  <option value="1">Artistic</option>
                  <option value="2">Sports</option>
                </select>
              </div>

              <div className="form-group mb-3">
                <label htmlFor="newsletterFile" className="form-label">
                  Upload Newsletter File (.png/.pdf)
                </label>
                <input
              className="form-control"             
              id="file"
              name="file"
              ref={inputRef}
              type="file"
              onChange={handlerFile}
            />
          </div>
            </div>
          </div>
          <div className="row">
            <div className="col-2 offset-3">
              <button
                type="submit"
                className="btn btn-primary  mt-3 mb-5"
                
              >
                Save Newsletter
              </button>
            </div>
            <div className="col-2 ">
              <button
                type="buttom"
                data-bs-toggle="modal"
                data-bs-target="#sendNesletter"
                className="btn btn-primary  mt-3 mb-5"
                onClick={sendNewsletter}
                disabled={sendDisabled}
              >
                Send Newsletter
              </button>
            </div>
          </div>
        </form>
        <Modal title="Newsletter- Email configuration" id="sendNesletter" />       
    
    </div>
  );
};
