import React, { useState,useEffect } from "react";
import {Modal} from '../commons/Modal';

export const RowNewsletter = ({neswsletter}) => {
 
  const [showSend, setshowSend]= useState(true);
  const [status, setEstatus]= useState("Sent");

 if(neswsletter.sentEstatus ===1)
  setEstatus("Sent");
 else   
 setEstatus("Wait");

  useEffect(() => {
  
    if(neswsletter.sentEstatus ===1 && neswsletter.sentDate===null){
      setshowSend(false);
  }
  else{
    setshowSend(true)
    
  }
 

  }, [status]);

    const Newsletter = () => {
        console.log("Newsletter");
    }
  return (
    <>
      <tr >
        <td>
          <button className="btn btn-primary btn-xs" 
          title="Send" 
          onClick={ Newsletter }
          data-bs-toggle="modal"
          data-bs-target="#mailConfig"
          >
            <span className="icon is-small">
              <i className="fa fa-send"></i>
            </span>
          </button>
        </td>
        <td>{neswsletter.newsletterName}</td>
        <td>{neswsletter.newsletterAttach}</td>
        <td>{neswsletter.creationDate}</td>
        <td>{neswsletter.sentDate}</td>
        <td>{status}</td>
      </tr>
      <Modal title="Newsletter List- Email configuration" id="mailConfig" />
    </>
  );
};
