import Axios from "axios";
import Swal from "sweetalert2";

function showAlert(icon, details) {
  Swal.fire({
    icon: icon,
    title: details.message,
    text: details.details,
  });
}

export const createNesletterType = async (nesletterType) => {
  let data = {};
  console.log(nesletterType);
  try {
    let res = await Axios.post("/newsletterType", nesletterType);
    data = res.data;
    showAlert("success", data);
  } catch (error) {
    if( data.length === 0 ) {
        const errorException = {
          message: "We have a problem",
          details: error.message,          
        };
            showAlert("error", errorException);
      }
      else
        showAlert("error", data);
  }
};
