import React from "react";
import { Header } from "../components/commons/Header";
import { NewsletterContext } from  '../contexts/newsletterContext';
import { useEffect,useContext} from "react";
export const Unsuscribed = () => {


  const {suscribedList,getAllSuscribed} = useContext ( NewsletterContext );

 console.log("entra0");
  /*useEffect(() => {
    console.log("entra1");
    getAllSuscribed("mipv1687@hotmail.com");
  }, []);
  
  */
 const handlerUnsuscribed = (e) =>{
    console.log("entra");
     e.preventDefault();
     console.log("entra");
     let paramUnsuscribed = new FormData();
     paramUnsuscribed.append("email", "mipv1687@hotmail.com");
     getAllSuscribed(paramUnsuscribed);
 }
  return (
          
    <div class="card mt-3">
    <div class="card-header">
      Unsuscribed
    </div>
    <div class="card-body">
      <h5 class="card-title">We will miss you</h5>
      <p class="card-text">If you are sure to unsubscribe, press the "view list" button to continue the process</p>
      {
      suscribedList.map( (index) => (
                      <>
                      
                      <li className="list-group-item"  key={index}>
                      <input
                        className="form-check-input me-1"
                        type="checkbox"
                        value= {index[0]}
                        id="firstCheckboxStretched"
                       
                      />
                      <label
                        className="form-check-label stretched-link"
                        htmlFor="firstCheckboxStretched"
                      >
                        {index[2]}
                      </label>
                    </li> 
                  </>
                  
      ))}
      <buttom class="btn btn-primary me-5" onclick= {handlerUnsuscribed}>View list</buttom>
      <buttom class="btn btn-primary">Unsuscribed</buttom>
    </div>
  </div>
  );
};
