import React, { createContext, useReducer } from "react";
import NewsletterReducer from "../reducer/NewsletterReducer";
import { GET_NEWSLETTERS, ADD_NEWSLETTER,ADD_MAIL_USER,GET_ALL_NEWSLETTER_TYPE,GET_ONE_NEWSLETTER ,GET_ALL_SUSCRIBED} from "../const/actionTypes";
import Axios from "axios";
import Swal from "sweetalert2";
import { API_BASE_URL } from '../config';

export const NewsletterContext = createContext();

export const NewsletterContextProvider = (props) => {
  const inicialState = {
    newsletterList: [],
    newsletterTypeList: [],
    suscribedList:[],
    currentData: {},
    currentDataList: {},

  };
  function showAlert(icon, details) {
    Swal.fire({
      icon: icon,
      title: details.message,
      text: details.details,
    });
   
  }
  //use reducer pra editar, crear eliminar, etc
  //dispathc método logado al state y que nos, es mediante ese método que indicaremos las acciones a realizar
  //para que realice el cambio en el valor del estado
  const [state, dispatch] = useReducer(NewsletterReducer, inicialState);

  //obtener todos los rows

  const getNewsletters = async () => {
    try {
      //const result = await Axios.get("/newsletter");
    
     let res = await Axios.get('/newsletter');

      let data = res.data;
      
      dispatch({
        type: GET_NEWSLETTERS, //que tipo es
        payload: data //que o cual es el valor que necesitamos modificar en el state
      });
    } catch (error) {
      console.log(error);
    }
  };
  const getAllNewsletterType = async () => {
    try {
      let res = await Axios.get('/newsletterType');
      dispatch({
        type: GET_ALL_NEWSLETTER_TYPE, //que tipo es
        payload: res.data //que o cual es el valor que necesitamos modificar en el state
      });
    } catch (error) {
      console.log(error);
    }
  };

  const getAllSuscribed = async (email) => {
    try {
      let res = await Axios.get(`/newsletterUser/${email}`);
      dispatch({
        type: GET_ALL_SUSCRIBED, //que tipo es
        payload: res.data //que o cual es el valor que necesitamos modificar en el state
      });
    } catch (error) {
      console.log(error);
    }
  };

  const getNewsletter = async (newsletter) => {
    try {
      //let res = await Axios.get('/newsletterType');
      dispatch({
        type: GET_ONE_NEWSLETTER, //que tipo es
        payload: newsletter //que o cual es el valor que necesitamos modificar en el state
      });
    } catch (error) {
      console.log(error);
    }
  };

  const addNewsletter2 = async(newsletter) => {
   
    try{
      //setLoading(true);
      const url =  `${API_BASE_URL}/newsletter`;
      const response = await fetch(url, {
        method: "POST",
        body: newsletter,
      });
      const data = await response.json();  
      dispatch({
            type: ADD_NEWSLETTER, //que tipo es
            payload: data //que o cual es el valor que necesitamos modificar en el state
       });
       if (response.ok) {
        showAlert("success", data);
        //setLoading(false);
      } else {
        showAlert("error", data);
       // setLoading(false);
      }
    }
    catch(error){
     
        const errorException = {
          message: "We have a problem",
          details: error.message,          
        }
      showAlert("error", errorException);
    
    }
    
  };

  const addNewsletter = async(newsletter) => {
    let data={};
    try{
    
      let res = await Axios.post("/newsletter",newsletter);

      let data = res.data;
     

        dispatch({
            type: ADD_NEWSLETTER, //que tipo es
            payload: data //que o cual es el valor que necesitamos modificar en el state
          });

          showAlert("success", data);
         
    }
    catch(error){
      if( data.length === 0 ) {
        const errorException = {
          message: "We have a problem",
          details: error.message,          
        };
        showAlert("error", errorException);
      }
      else
      showAlert("error", data);
    }
    
  };

  const unsuscribedNewsletter = async(unsuscribedList) => {
    
    if(unsuscribedList.length<0)
      return;
      
    let data={};
    try{
    
      let res = await Axios.put("/newsletterUser",unsuscribedList);
      let data = res.data;   
      window.location.reload();
      /*  dispatch({
            type: ADD_NEWSLETTER, //que tipo es
            payload: data //que o cual es el valor que necesitamos modificar en el state
          });
          */
          showAlert("success", "Unsuscribed Ok");
         
    }
    catch(error){
      console.log("error:"+error)
      if( data.length === 0 ) {
        const errorException = {
          message: "We have a problem",
          details: error.message,          
        };
        showAlert("error", errorException);
      }
      else
      showAlert("error", data);
    }
    
  };

  const addEmailUser2= async(param1) => {

    console.log("entra 2");
    let data ={};
    try{
    
      let res = await Axios.post("/user",param1);

      data = res.data;
      console.log("res "+res);

        dispatch({
            type: ADD_MAIL_USER, //que tipo es
            payload: data //que o cual es el valor que necesitamos modificar en el state
          });

          //showAlert("success", data);
          /*let res2 = await Axios.post("/email",param2);
          console.log(res2);
          let data2= res2.data;
          console.log(data2);
    
            dispatch({
                type: SEND_MAIL_USER, //que tipo es
                payload: data2 //que o cual es el valor que necesitamos modificar en el state
              });
    */
              showAlert("success", data);
         
    }
    catch(error){

      if( data.length === 0 ) {
        const errorException = {
          message: "We have a problem",
          details: error.message,          
        };
        showAlert("error", errorException);
      }
      else
      showAlert("success", data);
    
      
    }
    
  };
  const addEmailUser= async(paramsEmailUser, sendList) => {

    let data ={};
    try{

      if(sendList === 1){
        let res = await Axios.post("/users/uploadFile",paramsEmailUser);

        data = res.data;
        console.log("res "+res);
  

      }
      else{
        let res = await Axios.post("/users/user",paramsEmailUser);

        data = res.data;
        console.log("res "+res);
  

      }
    
   
        dispatch({
            type: ADD_MAIL_USER, //que tipo es
            payload: data //que o cual es el valor que necesitamos modificar en el state
          });

         
              showAlert("success", data);
         
    }
    catch(error){

      if( data.length === 0 ) {
        const errorException = {
          message: "We have a problem",
          details: error.message,          
        };
        showAlert("error", errorException);
      }
      else
      showAlert("success", data);
    
      
    }
    
  };

  
  return (
    <NewsletterContext.Provider
      value={{
        currentData:state.currentData,
        newsletterList: state.newsletterList,
        newsletterTypeList: state.newsletterTypeList,
        currentDataList:  state.currentDataList,
        suscribedList:state.suscribedList,
        

        getNewsletters,
        addNewsletter,
        addEmailUser,
        getAllNewsletterType,
        getNewsletter,
        getAllSuscribed,
        unsuscribedNewsletter
      }}
    >
      {props.children}
    </NewsletterContext.Provider>
  );
};
